import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts, getCategory } from "../actions";

// eslint-disable-next-line
import css from "../css/storeGrid.css";

const StoreGrid = ({ getProducts, getCategory, products, category }) => {
  useEffect(() => {
    getCategory();
    // eslint-disable-next-line
  }, []);

  const renderImages = () => {
    return category.map((item) => {
      return (
        <div className='col col-4 product' key={item.id}>
          <img src={item.image} alt={item.image} />
          <h6>{item.title}</h6>
          <h5>${item.price}</h5>
        </div>
      );
    });
  };

  return (
    <div className='container'>
      <div className='row'>{renderImages()}</div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
    category: state.category,
  };
};

export default connect(mapStateToProps, {
  getProducts,
  getCategory,
})(StoreGrid);
