import React, { Fragment } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { logout } from "../actions";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({ openOrClosed, authUser, logout }) => {
  const { isAuthenticated, user } = authUser;

  const onLogout = () => {
    logout();
  };

  const guestLinks = () => {
    return (
      <div className='slide-menu__user-login'>
        <Link to='/account/login'>
          <span>Login</span>
        </Link>
        <Link to='/account/sign-up'>
          <span>Sign Up</span>
        </Link>
      </div>
    );
  };

  const authLinks = () => {
    return (
      <div className='slide-menu__user-login'>
        <h1>Hello {user && user.name}</h1>
        <a onClick={onLogout} href='#!'>
          <svg
            xmlns='http://www.w3.org/2000/svg'
            className='h-6 w-6'
            fill='none'
            viewBox='0 0 24 24'
            stroke='currentColor'>
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              strokeWidth={2}
              d='M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1'
            />
          </svg>
          <span>Logout</span>
        </a>
      </div>
    );
  };

  const renderLinks = () => {
    return <Fragment>{isAuthenticated ? authLinks() : guestLinks()}</Fragment>;
  };

  const renderItems = () => {
    <div>
      <Link to='/'>
        <button className='btn navbar__icon--bg-color'>
          <i className='lni lni-home'>Home</i>
        </button>
      </Link>
      <Link to='/cart'>
        <button className='btn navbar__icon--bg-color'>
          <i className='lni lni-cart'>Cart</i>
        </button>
      </Link>
    </div>;
  };

  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle}`}>
      {renderLinks()}
      {renderItems()}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
    authUser: state.authUser,
  };
};

export default connect(mapStateToProps, {
  logout,
})(SlideMenu);
