import React, { createRef } from "react";
import { useRef, useMemo } from "react";
import { connect } from "react-redux";

// eslint-disable-next-line
import css from "../css/shoppingCartCard.css";

const ShoppingCartCard = ({ cartItems }) => {
  //! this might be only creating one ref and attaching that same ref to all the cards. all those refs though only refer to the same element which is the last item in the nodeList of cards.
  //? solution to this problem might be to create an array of refs and apply those refs to the array of cards being created
  // const ref = useRef(null);

  // const refs = useMemo(() => Array.from(cartItems).map(() => createRef()), []);

  // this creates an array of refs and store them to an array for each item in cartItems
  const refs = Array.from(cartItems, () => createRef(null));

  const onClick = () => {
