import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getSingleProduct } from "../actions";

const Product = ({ getSingleProduct }) => {
  useEffect(() => {
    // console.log(params.match.params.id);
    getSingleProduct();
  }, []);

  return <div>This is the product component</div>;
};

export default connect(null, { getSingleProduct })(Product);
