import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { getSingleProduct, addProductToCart, isItemInCart } from "../actions";
import { Link } from "react-router-dom";
import Loading from "./Loading";

// eslint-disable-next-line
import css from "../css/product.css";

//? bring in updateQuantity from cartItemReducer and run function for Buy It Now. This will take the user to their cart with all items present in cart but also with the item they just chose to 'buy now' with the updated values for quantity

const Product = (props) => {
  const { singleProduct, cartItems } = props;
  const { id } = props.match.params;

  const [number, setNumber] = useState(1);

  useEffect(() => {
    props.getSingleProduct(id);
    // eslint-disable-next-line
  }, []);

  if (!singleProduct) {
    return <Loading />;
  }

  const buyItNow = (e) => {
    e.preventDefault();
    console.log(singleProduct);
  };

  const addToCart = (e) => {
    e.preventDefault();

    const checkCart = (element) => element.id === singleProduct.id;

    //* this properly adds an item to the cartItems state with a quantity key/value and will check if an item is already inside the cart
    cartItems.some(checkCart)
      ? props.isItemInCart()
      : props.addProductToCart({ ...singleProduct, quantity: number });
  };

  const onChange = (e) => {
    setNumber(+e.target.value);
  };

  const increaseValue = () => {
    setNumber(number + 1);
  };

  const decreaseValue = () => {
    if (number === 0) {
      setNumber(0);
    } else {
      setNumber(number - 1);
    }
  };

  const renderProduct = () => {
    const { image, title, price, description, id, quantity } = singleProduct;

    return (
      <div className='card mb-3' style={{ maxWidth: "540px" }}>
        <div className='row g-0'>
          <div className='col-md-4'>
            <img src={image} alt={image} />
          </div>
          <div className='col-md-8'>
            <div className='card-body'>
              <h5 className='card-title'>{title}</h5>
              <p className='card-text'>{description}</p>
              <p className='card-text'>
                <small className='text-muted'>${price}</small>
              </p>
              <div>
                <div className='btn-group'>
                  <form onSubmit={(e) => buyItNow({ id, quantity })}>
                    <button className='btn btn-success'>Buy Now</button>
                  </form>

                  <form onSubmit={addToCart}>
                    <button className='btn btn-info'>Add to cart</button>
                    <input
                      name='product'
                      className='card-input'
                      min='1'
                      type='number'
                      onChange={onChange}
                      value={number}
                    />
                  </form>
                </div>

                <button
                  onClick={() => increaseValue()}
                  className='btn-increase'>
                  +
                </button>
                <button
                  onClick={() => decreaseValue()}
                  className='btn-decrease'>
                  -
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return <div className='container'>{renderProduct()}</div>;
};

const mapStateToProps = (state) => {
  return {
    singleProduct: state.singleProduct,
    cartItems: state.cartItems,
  };
};

export default connect(mapStateToProps, {
  getSingleProduct,
  addProductToCart,
  isItemInCart,
})(Product);
