import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getSingleProduct } from "../actions";

//! the component does not receive the singleProduct state in time to do a render
const Product = (props) => {
  const { singleProduct } = props;
  const { id } = props.match.params;

  useEffect(() => {
    console.log(props);
    // props.getSingleProduct(id);
    //? added a timeout to test the loading component. it seems this component is not receiving the singleProduct state in time for it to then render out some content.
    setTimeout(() => {
      props.getSingleProduct(id);
    }, 2000);
  }, []);

  if (!singleProduct) {
    return <div>Loading...</div>;
  }

  const renderProduct = () => {
    const { image, title, price, description } = singleProduct;

    return (
      <div>
        <img src={image} alt='' />
        <h1>{title}</h1>
        <h2>{price}</h2>
        <h3>{description}</h3>
      </div>
    );
  };

  return <div className='container'>{renderProduct()}</div>;
};

const mapStateToProps = (state) => {
  return {
    singleProduct: state.singleProduct,
  };
};

export default connect(mapStateToProps, { getSingleProduct })(Product);
