import { FETCH_PRODUCTS, FETCH_CATEGORY, SET_SLIDE_MENU } from "../types";

//get all products
export const getProducts = () => {
  return async (dispatch) => {
    const response = await fetch("https://fakestoreapi.com/products");
    const data = await response.json();

    dispatch({
      type: FETCH_PRODUCTS,
      payload: data,
    });
  };
};

// rewrite this to be modular and accept an argument that takes in any category
export const getCategory = (category) => {
  return async (dispatch) => {
    const response = await fetch(
      `https://fakestoreapi.com/products/category/${category}`
    );
    const data = await response.json();

    dispatch({
      type: FETCH_CATEGORY,
      payload: data,
    });
  };
};

// this is the endpoint to hit for categories
// fetch('https://fakestoreapi.com/products/category/jewelery')
//             .then(res=>res.json())
//             .then(json=>console.log(json))

export const showSlideMenu = () => {
  return {
    type: SET_SLIDE_MENU,
  };
};
