    setTimeout(() => {
      props.getSingleProduct(id);
    }, 2000);
  }, []);

  const renderProduct = () => {
    if (!singleProduct) {
      return <div>Loading...</div>;
    } else {
      return <div>hi</div>;
    }

    // return singleProduct.map((item) => {
