import axios from "axios";
import setAuthToken from "../utils/setAuthToken";
import { v4 as uuidv4 } from "uuid";

import {
  FETCH_SINGLE_PRODUCT,
  FETCH_PRODUCTS,
  FETCH_CATEGORY,
  SET_SLIDE_MENU,
  CATEGORY_SELECTIONS,
  CLEAR_PRODUCT,
  ADD_CART_ITEM,
  REMOVE_CART_ITEM,
  CART_ITEM_CHECK,
  CART_ERROR,
  UPDATE_QUANTITY,
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  LOAD_USER_CART,
  AUTH_ERROR,
  REMOVE_ALERT,
  SET_ALERT,
  CLEAR_ERRORS,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
} from "../types";

//open and close the slideMenu if true or false
export const showSlideMenu = () => {
  return {
    type: SET_SLIDE_MENU,
  };
};

//get all products
export const getProducts = (categories) => {
  return async (dispatch) => {
    const response = await fetch("https://fakestoreapi.com/products");

    const data = await response.json();

    dispatch({
      type: FETCH_PRODUCTS,
      payload: data,
    });
    dispatch({
      type: CLEAR_PRODUCT,
    });
  };
};

// fetch products by category
export const getCategory = (category) => {
  return async (dispatch) => {
    const response = await fetch(
      `https://fakestoreapi.com/products/category/${category}`
    );
    const data = await response.json();

    dispatch({
      type: FETCH_CATEGORY,
      payload: data,
    });
  };
};

// fetch a single product by its id
export const getSingleProduct = (id) => {
  return async (dispatch) => {
    const response = await fetch(`https://fakestoreapi.com/products/${id}`);
    const data = await response.json();

    dispatch({
      type: FETCH_SINGLE_PRODUCT,
      payload: data,
    });
  };
};

//map over the formProps obejct being passed in here and take every category value then assign an array made of those values to payload value
export const categorySelection = (formProps) => {
  // only add formProps value to the array if not null. if a value is null then remove it
  const formValues = Object.values(formProps).filter((item) => item !== null);

  return {
    type: CATEGORY_SELECTIONS,
    payload: formValues,
  };
};

//adds a product to the cartItems state
export const addProductToCart = (cartItem) => {
  return async (dispatch, getState) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const isAuthenticated = getState().authUser.isAuthenticated;

    try {
      if (isAuthenticated) {
        const res = await axios.post("/api/cart", cartItem, config);

        dispatch({
          type: ADD_CART_ITEM,
          payload: res.data,
        });
      } else {
        dispatch({
          type: ADD_CART_ITEM,
          payload: cartItem,
        });
      }
    } catch (err) {
      dispatch({
        type: CART_ERROR,
        payload: err.response.msg,
      });
    }
  };
};

//remove a product from the cartItems state
//! this is only being sent over the value id and not the value of _id from the ShoppingCartCard component. I have a value attribute on the cards that identifies them by id and was using this to removes items from the cartItems state.
export const removeProductFromCart = (cartItem) => {
  return async (dispatch, getState) => {
    const isAuthenticated = getState().authUser.isAuthenticated;

    try {
      if (isAuthenticated) {
        console.log(cartItem);
        const res = axios.delete(`/api/cart/${+cartItem}`);
        dispatch({
          type: REMOVE_CART_ITEM,
          payload: res.data,
        });
      } else {
        dispatch({
          type: REMOVE_CART_ITEM,
          payload: +cartItem,
        });
      }
    } catch (err) {
      dispatch({
        type: CART_ERROR,
        payload: err.response.msg,
      });
    }
  };

  //* this is the original code that works to dispatch before authentication was introduced
  // return {
  //   type: REMOVE_CART_ITEM,
  //   payload: +cartItem,
  // };
};

export const updateQuantity = ({ id, quantity }) => {
  return {
    type: UPDATE_QUANTITY,
    payload: { id, quantity },
  };
};

//checks if an item is already inside the cartItems state by matching the id
export const isItemInCart = () => {
  alert("That item is already in cart");
  return {
    type: CART_ITEM_CHECK,
  };
};

export const loadUserCart = () => {
  return async (dispatch) => {
    const res = await axios.get("/api/cart");

    dispatch({
      type: LOAD_USER_CART,
      payload: res.data,
    });
  };
};

// Load user
export const loadUser = () => {
  return async (dispatch) => {
    setAuthToken(localStorage.token);

    try {
      const res = await axios.get("/api/auth");

      dispatch({
        type: USER_LOADED,
        payload: res.data,
      });
    } catch (err) {
      dispatch({ type: AUTH_ERROR });
    }
  };
};

// Register user
export const registerUser = (user) => {
  return async (dispatch) => {
    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };

    try {
      const res = await axios.post("/api/users", user, config);

      dispatch({
        type: REGISTER_SUCCESS,
        payload: res.data,
      });

      loadUser();
    } catch (err) {
      dispatch({
        type: REGISTER_FAIL,
        payload: err.response.data.msg,
      });
    }
  };
};

// Login user
export const login = (user) => {
  return async (dispatch) => {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    try {
      const res = await axios.post("/api/auth", user, config);

      dispatch({
        type: LOGIN_SUCCESS,
        payload: res.data,
      });

      loadUser();
    } catch (err) {
      dispatch({
        type: LOGIN_FAIL,
        payload: err.response.data.msg,
      });
    }
  };
};

// Logout User
export const logout = () => {
  return {
    type: LOGOUT,
  };
};

// Set alert
export const setAlert = (msg, type, timeout = 5000) => {
  const id = uuidv4();
  return async (dispatch) => {
    dispatch({
      type: SET_ALERT,
      payload: { msg, type, id },
    });

    setTimeout(() => dispatch({ type: REMOVE_ALERT, payload: id }), timeout);
  };
};

export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS,
  };
};
