import { FETCH_PRODUCTS, SET_SLIDE_MENU } from "../types";

export const getProducts = () => {
  return async (dispatch) => {
    const response = await fetch("https://fakestoreapi.com/products");
    const data = await response.json();

    // const albumArr = await data
    //   .filter((album) => album.albumId === 1)
    //   .map((photo) => {
    //     return { image: photo.thumbnailUrl, id: photo.id };
    //   });

    dispatch({
      type: FETCH_PRODUCTS,
      payload: data,
    });
  };
};

export const showSlideMenu = (state) => {
  console.log(state);
  return {
    type: SET_SLIDE_MENU,
  };
};
