import { CART_ITEM } from "../types";

const cartItemReducer = (state = [], action) => {
  const cartCheck = state
    .map((item) => {
      return item;
    })
    .filter((item) => {
      return action.payload;
    });

  switch (action.type) {
    case CART_ITEM:
      return [...state, cartCheck];
    default:
      return state;
  }
};

export default cartItemReducer;
