import React from "react";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";
import { categorySelection } from "../actions";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({
  openOrClosed,
  handleSubmit,
  productCategories,
  categorySelection,
}) => {
  const renderInput = () => {
    return productCategories.map((category, index) => {
      return (
        <div className='form-check' key={index}>
          <Field
            name={category.category}
            component='input'
            className='form-check-input'
            type='checkbox'
            format={(v) => v === `${category.value}`}
            // converts redux state string to boolean
            normalize={(v) => (v ? `${category.value}` : null)}
            // converts checkbox boolean to string
            // value={category.category}
          />
          <label className='form-check-label' htmlFor='flexCheckDefault'>
            {category.value}
          </label>
        </div>
      );
    });
  };

  // send the formProps object over to state where in the Object.values(formProps) turns the formProps object's values into an array
  const submitHandler = (formProps) => {
    categorySelection(formProps);
  };

  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle} container--flex`}>
      <h5>Filter By Category</h5>
      <form onSubmit={handleSubmit(submitHandler)}>
        {renderInput()}
        <button className='btn btn--secondary'>Apply Filter</button>
      </form>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    productCategories: state.productCategories,
    openOrClosed: state.openOrClosed,
  };
};

const slideMenu = connect(mapStateToProps, {
  categorySelection,
})(SlideMenu);

export default reduxForm({
  form: "slideMenu",
})(slideMenu);
