import React, { Fragment, useEffect, useRef } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import {
  showSlideMenu,
  logout,
  loadUser,
  loadUserCart,
  getCategory,
} from "../../actions";

// eslint-disable-next-line
import css from "../../css/navbar.css";

const Navbar = ({ loadUser, logout, showSlideMenu, authUser, getCategory }) => {
  const { isAuthenticated, user } = authUser;

  const ref = useRef();
  const ref2 = useRef();

  useEffect(() => {
    loadUser();
    // eslint-disable-next-line
  }, [isAuthenticated]);

  const onLogout = () => {
    logout();
  };

  const open = () => {
    document.body.classList.toggle("open");
  };

  const onMouseOver = () => {
    ref.current.classList.add("play");
    ref2.current.classList.add("show");
  };

  const onMouseOut = () => {
    ref.current.classList.remove("play");
    ref.current.classList.add("paused");
    ref2.current.classList.remove("show");
    setTimeout(() => {
      ref.current.classList.remove("paused");
    }, 300);
  };

  const onClick = (e) => {
    getCategory(e.target.getAttribute("value"));
  };

  const guestLinks = () => {
    return (
      <Fragment>
        <Link to='/account/login'>
          <button className='btn btn--primary'>Login</button>
        </Link>
        <Link to='/account/sign-up'>
          <button className='btn btn--secondary'>Sign Up</button>
        </Link>
      </Fragment>
    );
  };

  const authLinks = () => {
    return (
      <div className='navbar__user-login'>
        <h1>Hello {user && user.name}</h1>
        <a onClick={onLogout} href='#!'>
          <svg
            xmlns='http://www.w3.org/2000/svg'
            className='h-6 w-6'
            fill='none'
            viewBox='0 0 24 24'
            stroke='currentColor'>
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              strokeWidth={2}
              d='M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1'
            />
          </svg>
          <span>Logout</span>
        </a>
      </div>
    );
  };

  const renderedItems = () => {
    return (
      <Fragment>
        <div className='navbar__title-group'>
          <button
            onClick={() => {
              showSlideMenu();
              open();
            }}
            className='btn hamburger navbar__icon--bg-color'>
            <i className='lni lni-menu'></i>
          </button>

          <h1 className='navbar__title'>Store Name</h1>

          <div
            onMouseOver={onMouseOver}
            onMouseOut={onMouseOut}
            className='navbar__categories ml-1'>
            Categories
            <i ref={ref} className='lni lni-chevron-down ml-1'></i>
            <div ref={ref2} className='navbar__dropdown'>
              <ul className='navbar__list'>
                <li
                  onClick={onClick}
                  value="men's clothing"
                  className='navbar__list-item mb-1'>
                  Mens Clothing
                </li>
                <li
                  onClick={onClick}
                  value="women's clothing"
                  className='navbar__list-item mb-1'>
                  Womens Clothing
                </li>
                <li
                  onClick={onClick}
                  value='jewlery'
                  className='navbar__list-item mb-1'>
                  Jewlery
                </li>
                <li
                  onClick={onClick}
                  value='electronics'
                  className='navbar__list-item mb-1'>
                  Electronics
                </li>
              </ul>
            </div>
          </div>
        </div>

        <div className='navbar__btn-group'>
          <Link to='/'>
            <button className='btn navbar__icon--bg-color'>
              <i className='lni lni-home'></i>
            </button>
          </Link>
          <Link to='/cart'>
            <button className='btn navbar__icon--bg-color'>
              <i className='lni lni-cart'></i>
            </button>
          </Link>
          {isAuthenticated ? authLinks() : guestLinks()}
        </div>
      </Fragment>
    );
  };

  return <div className='navbar bg-dark'>{renderedItems()}</div>;
};

const mapStateToProps = (state) => {
  return {
    authUser: state.authUser,
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps, {
  showSlideMenu,
  logout,
  loadUser,
  loadUserCart,
  getCategory,
})(Navbar);
