import React, { createRef, useState } from "react";
import { connect } from "react-redux";
import { removeProductFromCart, updateQuantity } from "../actions";

// eslint-disable-next-line
import css from "../css/shoppingCartCard.css";

//Todo turn the onClick for the updateQuantity button into something that changes an element into an iput the user can interact with. Maybe do away with the button and the quantity display and add a select tag with quantitys as the options tags.
const ShoppingCartCard = (props) => {
  const { cartItems, removeProductFromCart, updateQuantity } = props;
  const [quantity, setQuantity] = useState("");

  //! wont need this if i get the select working
  const [input, setInput] = useState(false);

  // this creates an array of all the refs and stores them to an array for each item in cartItems
  const refs = Array.from(cartItems, () => createRef(null));

  // this handler loops through the refs and checks that they match the delete button's data-key and removes the item from the dom and cartItems
  const deleteFromCart = (e) => {
    refs.forEach((ref) => {
      if (
        ref.current.getAttribute("value") === e.target.getAttribute("data-key")
      ) {
        removeProductFromCart(ref.current.getAttribute("value"));
      }
    });
  };

  //! needs to update quantity after a user gives some input
  //? this is the onSubmit function for form after a user has given some input for quantity change
  //? think i need to split out this logic. the upper portion that is doing the check in the if should trigger to allow for a single item to change its input
  const updateCart = (e) => {
    e.preventDefault();
    refs.forEach((ref) => {
      if (
        //! this might not match to anything unless i use data-key on the select options
        ref.current.getAttribute("value") === e.target.getAttribute("data-key")
      ) {
        updateQuantity({
          id: +ref.current.getAttribute("value"),
          quantity: quantity,
        });
      }
    });
    //! delete this changeQuantityField out of here if the select with options works
    // changeQuantityField();
    console.log(quantity);
  };

  //toggles between an input field and a p element in renderedItems
  //! wont need this if i get the select working
  const changeQuantityField = () => {
    setInput(!input);
  };

  //! save this function incase the select and options method doesnt work out
  // const renderInput = () => {
  //   return (
  //     <form onSubmit={updateCart}>
  //       <input type='number' value={quantity} onChange={onChange} />
  //     </form>
  //   );
  // };

  //! get this working to render 20 option tags to render into each select element per cartCard
  const renderInput = () => {
    // for (let i = 0; i <= 20; i++) {
    //   return <option>{[i]}</option>;
    // }

    const options = 20;

    const array = [];

    //! options is not an array -_-
    // options.map((item) => array.push(<option>{item}</option>));

    return array;
  };

  //? its under this function that i need to isolate the div that i want to change the input
  //! save this function incase the select and options method doesnt work out
  const testFunction = (e) => {
    refs.forEach((ref) => {
      if (
        ref.current.getAttribute("value") === e.target.getAttribute("data-key")
      ) {
        changeQuantityField();
        renderInput();
      }
    });
  };

  const onChange = (e) => setQuantity(e.target.value);

  const renderItems = () => {
    return cartItems.map((cartItem, i) => {
      return (
        <div
          ref={refs[i]}
          key={cartItem.id}
          value={cartItem.id}
          className='cart-card'>
          <div className='cart-card-img'>
            <img src={cartItem.image} alt='' />
          </div>
          <div className='cart-card-body'>
            <h5>{cartItem.title}</h5>
            <p>${cartItem.price}</p>
            {/* //* keep this p to input stuff incase the select with options does not work out
             */}
            {/* {input ? (
              <form onSubmit={updateCart}>
                <input type='number' value={quantity} onChange={onChange} />
              </form>
            ) : (
              <p>Quantity: {cartItem.quantity}</p>
            )} */}
            {/* {input ? renderInput() : <p>Quantity: {cartItem.quantity}</p>} */}
            {/* //? trying out a select with options for quantity instead */}
            <form>
              <select name='Quantity'>
                {/* <option value='1'>Quantity: 1</option> */}
                {renderInput()}
              </select>
            </form>
            <div className='button-group'>
              {/* <button
                onClick={testFunction}
                data-key={cartItem.id}
                className='btn btn-primary'>
                Update Quantity
              </button> */}
              <button
                onClick={deleteFromCart}
                data-key={cartItem.id}
                className='btn btn-danger'>
                Delete From Cart
              </button>
            </div>
          </div>
        </div>
      );
    });
  };

  return <div className='container'>{renderItems()}</div>;
};

const mapStateToProps = (state) => {
  return {
    cartItems: state.cartItems,
  };
};

export default connect(mapStateToProps, {
  removeProductFromCart,
  updateQuantity,
})(ShoppingCartCard);
