import React, { createRef } from "react";
import { useRef, useMemo } from "react";
import { connect } from "react-redux";

// eslint-disable-next-line
import css from "../css/shoppingCartCard.css";

const ShoppingCartCard = ({ cartItems }) => {
  //! this might be only creating one ref and attaching that same ref to all the cards. all those refs though only refer to the same element which is the last item in the nodeList of cards.
  //? solution to this problem might be to create an array of refs and apply those refs to the array of cards being created
  const ref = useRef(null);

  // const refs = useMemo(() => Array.from(cartItems).map(() => createRef()), []);

  const refs = Array.from(cartItems.length, (a) => createRef(null));

  const onClick = () => {
    console.log(ref.current);
  };

  const handleClick = () => {
    // console.log(ref.current.getAttribute("value"));
    // console.log(ref.current);
  };

  const renderItems = () => {
    return cartItems.map((cartItem, i) => {
      return (
        <div
          // onClick={handleClick}
          // ref={(ref) => {
          //   ref.current.push(ref);
          // }}
          ref={refs[i]}
          key={cartItem.id}
          value={cartItem.id}
          className='cart-card'>
          <div className='cart-card-img'>
            <img src={cartItem.image} alt='' />
          </div>
          <div className='cart-card-body'>
            <h5>{cartItem.title}</h5>
            <p>${cartItem.price}</p>
            <p>Quantity: {cartItem.quantity}</p>
            <div className='button-group'>
              <button className='btn btn-primary'>Update Quantity</button>
              <button onClick={onClick} className='btn btn-danger'>
                Delete From Cart
              </button>
            </div>
          </div>
        </div>
      );
    });
  };

  return <div className='container'>{renderItems()}</div>;
};

const mapStateToProps = (state) => {
  return {
    cartItems: state.cartItems,
  };
};

export default connect(mapStateToProps)(ShoppingCartCard);
