import React, { useState } from "react";

// eslint-disable-next-line
import css from "../../css/signUp.css";

//? Copy in the register component from the React-Front-To-Back/Contact-Keeper project for a better setup register form. It's located in the src/components/auth/Register.js

const SignUp = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  });

  const { name, email, password, password2 } = user;

  const onChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    console.log("submit user");
  };

  return (
    <div className='sign-up__container flex-center'>
      {/* the outside black part */}
      <div className='sign-up__card flex-center'>
        {/* the inside blue part */}
        <div className='sign-up__card-bg'>
          <div className='sign-up__card-group flex-center flex-dc'>
            <p className='sign-up__font--white'>Create an account</p>
            <form className='sign-up__form flex-center flex-dc'>
              <input
                className='sign-up__input'
                type='text'
                placeholder='Enter a username'
              />
              <input
                className='sign-up__input'
                type='password'
                placeholder='Enter a password'
              />
              <input
                className='sign-up__input'
                type='password'
                placeholder='Confirm password'
              />
              <div className='sign-up__button-group'>
                <p className='sign-up__password-match'>Passwords must match</p>
                <button className='btn btn--secondary'>Submit</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
