import { combineReducers } from "redux";
import { reducer as formReducer } from "redux-form";

import productsReducer from "./productsReducer";
import setSlideMenuReducer from "./setSlideMenuReducer";
import categoryReducer from "./categoryReducer";
import productCategoriesReducer from "./productCategoryReducer.js";
import categorySelectionReducer from "./categorySelectionReducer";
import singleProductReducer from "./singleProductReducer";
import cartItemReducer from "./cartItemReducer";
import authReducer from "./authReducer";
import alertsReducer from "./alertsReducer";

export default combineReducers({
  openOrClosed: setSlideMenuReducer,
  products: productsReducer,
  singleProduct: singleProductReducer,
  category: categoryReducer,
  categories: categorySelectionReducer,
  productCategories: productCategoriesReducer,
  //* all items inside the cart
  cartItems: cartItemReducer,
  authUser: authReducer,
  alerts: alertsReducer,
  form: formReducer,
});
