import React from "react";
import { connect } from "react-redux";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({ openOrClosed }) => {
  const renderInput = () => {
    return <div>new form</div>;
  };

  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle} container-flex flex-dc`}>{renderInput()}</div>
  );
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps)(SlideMenu);
