import _ from "lodash";

import {
  ADD_CART_ITEM,
  REMOVE_CART_ITEM,
  UPDATE_QUANTITY,
  CART_ITEM_CHECK,
  CART_ERROR,
  LOAD_USER_CART,
} from "../types";

const cartItemReducer = (
  state = [
    // {
    //   id: 1,
    //   title: "Fjallraven - Foldsack No. 1 Backpack, Fits 15 Laptops",
    //   price: 109.95,
