import { CATEGORY_SELECTIONS } from "../types";

//this reducer takes the formProps values and places them into an array.
//? I want that array to then be looped over by the getCategory action creator and make a combined array of all products for the storeGrid to then
const categorySelectionReducer = (state = [], action) => {
  switch (action.type) {
    case CATEGORY_SELECTIONS:
      return action.payload;
    default:
      return state;
  }
};

export default categorySelectionReducer;
