import React from "react";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";
import { getCategory } from "../actions";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({ openOrClosed, getCategory, handleSubmit }) => {
  console.log(props);
  const productCategories = [
    {
      category: "menClothing",
      value: "men clothing",
    },
    {
      category: "womenClothing",
      value: "women clothing",
    },
    {
      category: "jewelery",
      value: "jewelery",
    },
    {
      category: "electronics",
      value: "electronics",
    },
  ];

  const renderInput = () => {
    return productCategories.map((category, index) => {
      return (
        <div className='form-check' key={index}>
          <Field
            name={category.category}
            component='input'
            className='form-check-input'
            type='checkbox'
            format={(v) => v === `${category.value}`}
            // converts redux state string to boolean
            normalize={(v) => (v ? `${category.value}` : "0")}
            // converts checkbox boolean to string
            // value={category.category}
          />
          <label className='form-check-label' htmlFor='flexCheckDefault'>
            {category.value}
          </label>
        </div>
      );
    });
  };

  // this submit handler receives props from the Field element that passes props to the form element
  const submitHandler = (formProps) => {
    //how can formProps be looped over to check the value of the propnand extract that data?
    console.log(formProps);

    // this kind of works but not as expected. it only displays the last box checked
    if (formProps.electronics === "electronics") {
      getCategory(formProps.electronics);
    } else if (formProps.jewelery === "jewelery") {
      getCategory(formProps.jewelery);
    } else if (formProps.menClothing === "men clothing") {
      getCategory(formProps.menClothing);
    } else if (formProps.womenClothing === "women clothing") {
      getCategory(formProps.womenClothing);
    }
  };

  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle} container`}>
      <h5>Filter By Category</h5>
      <form onSubmit={handleSubmit(submitHandler)}>
        {renderInput()}
        <button className='btn btn-primary'>Apply Filter</button>
      </form>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    productCategories: state.productCategories,
    openOrClosed: state.openOrClosed,
  };
};

// this validate function is only boilerplate. the title and description values need to be changed to the name props of the checkboxes. make sure that atleast one checkbox is checked off before a user hits the submit button.
const validate = (formProps) => {
  const errors = {};

  if (!formProps.Electronics) {
    errors.title = "You must enter a title";
  }

  if (!formProps.description) {
    errors.description = "You must enter a description";
  }

  return errors;
};

const slideMenu = connect(mapStateToProps, {
  getCategory,
})(SlideMenu);

export default reduxForm({
  form: "slideMenu",
  validate: validate,
})(slideMenu);
