import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts } from "../actions";
import { Link } from "react-router-dom";
import Loading from "./Loading";
// eslint-disable-next-line
import css from "../css/storeGrid.css";

const StoreGrid = ({ getProducts, products, categories }) => {
  // anytime categories state has been loaded by a user making selections from the checkbox, the getCategory fetch call will be made.
  useEffect(() => {
    getProducts();
    // eslint-disable-next-line
  }, []);

  // this function here that will take the products, match them to categories, then render out those sorted products
  const sortedProducts = () => {
    const sortedProducts = products.filter((item) =>
      categories.includes(item.category)
    );
    return sortedProducts.map((item) => {
      return (
        <div className='card' key={item.id}>
          <Link
            className='card__link'
            key={item.id}
            to={`/products/${item.id}`}>
            <img className='card__img' src={item.image} alt={item.image} />
            <div className='card__content'>
              <p className='card__title'>{item.title}</p>
              <h1 className='card__price'>${item.price}</h1>
            </div>
          </Link>
        </div>
      );
    });
  };

  const renderImages = () => {
    return products.map((item) => {
      return (
        <div className='card' key={item.id}>
          <Link
            className='card__link'
            key={item.id}
            to={`/products/${item.id}`}>
            <img className='card__img' src={item.image} alt={item.image} />
            <div className='card__content'>
              <h5>{item.title}</h5>
              <h1 className='card__price'>${item.price}</h1>
            </div>
          </Link>
        </div>
      );
    });
  };

  if (!products) {
    return <Loading />;
  }

  return (
    <div className='container-grid col-1 col-2 col-4'>
      {categories.length === 0 ? renderImages() : sortedProducts()}
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
    categories: state.categories,
  };
};

export default connect(mapStateToProps, {
  getProducts,
})(StoreGrid);
