import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getSingleProduct } from "../actions";
import singleProductReducer from "../reducers/singleProductReducer";

const Product = (props) => {
  const { singleProduct } = props;
  const { id } = props.match.params;

  useEffect(() => {
    console.log(props);
    props.getSingleProduct(id);
  }, []);

  const renderProduct = () => {
    if (!singleProduct) {
      return <div>Loading...</div>;
    }

    return singleProduct.map((item) => {
      return (
        <div>
          <img src={item.image} alt='' />
          <h1>{item.title}</h1>
          <h2>{item.price}</h2>
          <h3>{item.description}</h3>
        </div>
      );
    });
  };

  return <div className='container'>{renderProduct()}</div>;
};

const mapStateToProps = (state) => {
  return {
    singleProduct: state.singleProduct,
  };
};

export default connect(mapStateToProps, { getSingleProduct })(Product);
