import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getSingleProduct } from "../actions";

const Product = (props) => {
  const { singleProduct } = props;
  const { id } = props.match.params;

  useEffect(() => {
    console.log(props);
    props.getSingleProduct(id);
    // eslint-disable-next-line
  }, []);

  if (!singleProduct) {
    return <div>Loading...</div>;
  }

  const renderProduct = () => {
    const { image, title, price, description } = singleProduct;

    //TODO: refactor this into something nicer
    // return (
    //   <div>
    //     <img src={image} alt='' />
    //     <h1>{title}</h1>
    //     <h2>{price}</h2>
    //     <h3>{description}</h3>
    //   </div>
    // );

    //* refactoring the above into something nicer
    return (
      <div class='card mb-3' style={{ maxWidth: "540px" }}>
        <div class='row g-0'>
          <div class='col-md-4'>
            <img src={image} alt={image} />
          </div>
          <div class='col-md-8'>
            <div class='card-body'>
              <h5 class='card-title'>{title}</h5>
              <p class='card-text'>{description}</p>
              <p class='card-text'>
                <small class='text-muted'>${price}</small>
              </p>
              <button className='btn btn-success'>Buy Now</button>
              <div>
                <button className='btn'>Quantity</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return <div className='container'>{renderProduct()}</div>;
};

const mapStateToProps = (state) => {
  return {
    singleProduct: state.singleProduct,
  };
};

export default connect(mapStateToProps, { getSingleProduct })(Product);
