import React, { useRef } from "react";
import { connect } from "react-redux";
import { showSlideMenu } from "../actions";

// eslint-disable-next-line
import css from "../css/header.css";

const Header = (props) => {
  const ref = useRef("clicked");

  const testRef = () => {
    console.log(ref.current);
  };

  const renderedItems = () => {
    return (
      <div className='container-fluid'>
        <button
          onClick={() => props.showSlideMenu()}
          ref={testRef}
          className='btn'>
          <i className='lni lni-menu'></i>
        </button>

        <div className='header'>Store Name</div>

        <div className=''>
          <button className='btn btn-light'>Login</button>
          <button className='btn btn-primary'>Sign Up</button>
        </div>
      </div>
    );
  };

  return <div className='navbar bg-dark'>{renderedItems()}</div>;
};

const mapStateToProps = (state, ownProps) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps, {
  showSlideMenu,
})(Header);
