import React, { useState, useEffect } from "react";

const StoreGrid = () => {
  // after this logic works i want to move it out into Redux store and import here as props
  const [images, setImage] = useState([]);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/photos/")
      .then((response) => response.json())
      .then((data) => {
        // this filters out the albumId of 1, maps over them and sets the state to an array of the thumbnailUrl and id
        const albumArr = data
          .filter((album) => album.albumId === 1)
          .map((photo) => {
            return { image: photo.thumbnailUrl, id: photo.id };
          });

        setImage(albumArr);
      });
  }, []);

  const renderImages = () => {
    return images.map((photo) => {
      return <img src={photo.image} alt='' />;
    });
  };

  return <div>{renderImages()}</div>;
};

export default StoreGrid;
