import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts, getCategory } from "../actions";

// eslint-disable-next-line
import css from "../css/storeGrid.css";

const StoreGrid = ({
  getProducts,
  getCategory,
  products,
  category,
  categories,
}) => {
  // anytime categories state has been loaded by a user making selections from the checkbox, the getCategory fetch call will be made.
  // this might not be the correct approach as everytime the component is loaded the getProducts call is made and the getCategory seems to only be called after the user has submitted their choice twice
  useEffect(() => {
    getProducts();
    // return () =>
    //   categories.map((category) => {
    //     getCategory(category);
    //   });
    // eslint-disable-next-line
  }, [categories]);

  //render the contents of the category state to the page.
  const renderCategories = () => {
    category.map((category) => {});
  };

  const renderImages = () => {
    return products.map((item) => {
      return (
        <div className='col col-4 product' key={item.id}>
          <img src={item.image} alt={item.image} />
          <h6>{item.title}</h6>
          <h5>${item.price}</h5>
        </div>
      );
    });
  };

  return (
    <div className='container'>
      <div className='row'>{renderImages()}</div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
    category: state.category,
    categories: state.categories,
  };
};

export default connect(mapStateToProps, {
  getProducts,
  getCategory,
})(StoreGrid);
