import _ from "lodash";

import {
  ADD_CART_ITEM,
  REMOVE_CART_ITEM,
  UPDATE_QUANTITY,
  CART_ITEM_CHECK,
  CART_ERROR,
  LOAD_USER_CART,
} from "../types";

const cartItemReducer = (state = [], action) => {
  switch (action.type) {
    case ADD_CART_ITEM:
    case LOAD_USER_CART:
      return [...state, action.payload];
    case REMOVE_CART_ITEM:
      return state.filter((item) => item.id !== action.payload);
    case CART_ITEM_CHECK:
      return state;
    case UPDATE_QUANTITY:
      const foundItem = _.find(state, { id: action.payload.id });
      const newItem = { ...foundItem, quantity: action.payload.quantity };
      const filteredState = state.filter(
        (item) => item.id !== action.payload.id
      );
      return [...filteredState, newItem];
    case CART_ERROR:
      return action.payload;
    default:
      return state;
  }
};

export default cartItemReducer;
