import { values } from "lodash";
import {
  FETCH_SINGLE_PRODUCT,
  FETCH_PRODUCTS,
  FETCH_CATEGORY,
  SET_SLIDE_MENU,
  CATEGORY_SELECTIONS,
  CLEAR_PRODUCT,
  ADD_CART_ITEM,
  REMOVE_CART_ITEM,
  CART_ITEM_CHECK,
  UPDATE_QUANTITY,
} from "../types";

//open and close the slideMenu if true or false
export const showSlideMenu = () => {
  return {
    type: SET_SLIDE_MENU,
  };
};

//get all products
export const getProducts = (categories) => {
  return async (dispatch) => {
    const response = await fetch("https://fakestoreapi.com/products");
    const data = await response.json();

    dispatch({
      type: FETCH_PRODUCTS,
      payload: data,
    });
    dispatch({
      type: CLEAR_PRODUCT,
    });
  };
};

// fetch products by category
export const getCategory = (category) => {
  return async (dispatch) => {
    const response = await fetch(
      `https://fakestoreapi.com/products/category/${category}`
    );
    const data = await response.json();

    dispatch({
      type: FETCH_CATEGORY,
      payload: data,
    });
  };
};

// fetch a single product by its id
export const getSingleProduct = (id) => {
  return async (dispatch) => {
    const response = await fetch(`https://fakestoreapi.com/products/${id}`);
    const data = await response.json();

    dispatch({
      type: FETCH_SINGLE_PRODUCT,
      payload: data,
    });
  };
};

//map over the formProps obejct being passed in here and take every category value then assign an array made of those values to payload value
export const categorySelection = (formProps) => {
  // only add formProps value to the array if not null. if a value is null then remove it
  const formValues = Object.values(formProps).filter((item) => item !== null);

  return {
    type: CATEGORY_SELECTIONS,
    payload: formValues,
  };
};

//adds a product to the cartItems state
export const addProductToCart = (cartItem) => {
  return {
    type: ADD_CART_ITEM,
    payload: cartItem,
  };
};

//remove a product from the cartItems state
export const removeProductFromCart = (cartItem) => {
  return {
    type: REMOVE_CART_ITEM,
    payload: +cartItem,
  };
};

//! currently only bringing in the quantity value. need to also bring in the item's id so as to be able to match it over in the reducer and give the item with corresponding id the correct quantity
export const updateQuantity = ({ id, quantity }) => {
  return {
    type: UPDATE_QUANTITY,
    payload: { id, quantity },
  };
};

//checks if an item is already inside the cartItems state by matching the id
export const isItemInCart = () => {
  return {
    type: CART_ITEM_CHECK,
  };
};
