import {
  ADD_CART_ITEM,
  REMOVE_CART_ITEM,
  UPDATE_QUANTITY,
  CART_ITEM_CHECK,
} from "../types";

const cartItemReducer = (
  state = [
    {
      id: 1,
      title: "Fjallraven - Foldsack No. 1 Backpack, Fits 15 Laptops",
      price: 109.95,
      description:
        "Your perfect pack for everyday use and walks in the forest. Stash your laptop (up to 15 inches) in the padded sleeve, your everyday",
      category: "men clothing",
      image: "https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg",
    },
    {
      id: 2,
      title: "Mens Casual Premium Slim Fit T-Shirts ",
      price: 22.3,
      description:
        "Slim-fitting style, contrast raglan long sleeve, three-button henley placket, light weight & soft fabric for breathable and comfortable wearing. And Solid stitched shirts with round neck made for durability and a great fit for casual fashion wear and diehard baseball fans. The Henley style round neckline includes a three-button placket.",
      category: "men clothing",
      image:
        "https://fakestoreapi.com/img/71-3HjGNDUL._AC_SY879._SX._UX._SY._UY_.jpg",
    },
    {
      id: 3,
      title: "Mens Cotton Jacket",
      price: 55.99,
      description:
        "great outerwear jackets for Spring/Autumn/Winter, suitable for many occasions, such as working, hiking, camping, mountain/rock climbing, cycling, traveling or other outdoors. Good gift choice for you or your family member. A warm hearted love to Father, husband or son in this thanksgiving or Christmas Day.",
      category: "men clothing",
      image: "https://fakestoreapi.com/img/71li-ujtlUL._AC_UX679_.jpg",
    },
  ],
  action
) => {
  switch (action.type) {
    case ADD_CART_ITEM:
      return [...state, action.payload];
    case REMOVE_CART_ITEM:
      return state.filter((item) => item.id !== action.payload);
    case CART_ITEM_CHECK:
      return state;
    case UPDATE_QUANTITY:
      //!action.payload currently coming in is the quantity number coming in. it can't be used to match to item.id
      //? i want to add into the object i find { quantity: action.payload }
      //? something like
      //?  state
      //? .map( item => ( {..item, quantity: action.payload} ) )
      const checkQuantity = (item) => item.id === 1;
      //! this returns a new object into the state array with just the quantity key
      // return state.some(checkQuantity) ? [...state, { quantity: action.payload }] : state;
      //! this returns state.some().map is not a function
      // return state
      //   .some(checkQuantity)
      //   .map((item) => ({ ...item, quantity: action.payload }));
      // return state.find((item) => {
      //   if (item.id === 1) {
      //     return [...state, { ...item, quantity: action.payload }];
      //   }
      // });
      //! this causes errors
      // return state.find((item) => item.id === 1);
      return state.map((item) => {
        if (item.id === 1) {
          return { ...item, quantity: action.payload };
        }
      });
    default:
      return state;
  }
};

export default cartItemReducer;
