import { FETCH_SINGLE_PRODUCT } from "../types";

const singleProductReducer = (state = null, action) => {
  switch (action.type) {
    case FETCH_SINGLE_PRODUCT:
      return action.payload;
    default:
      return state;
  }
};

export default singleProductReducer;
