import React from "react";
import { connect } from "react-redux";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({ openOrClosed }) => {
  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return <div className={toggle}>SlideMenu</div>;
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps)(SlideMenu);
