import { REGISTER_SUCCESS, REGISTER_FAIL } from "../types";

const authReducer = (state = [], action) => {
  switch (action.type) {
    case REGISTER_SUCCESS:
      return action.payload;
    default:
      return state;
  }
};

export default authReducer;
