  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/photos/")
      .then((response) => response.json())
      .then((data) => {
        //   this map sets only the last image from the api call
        // data.map((photo) =>
        //   setImage({ image: photo.thumbnailUrl, id: photo.id })
        // );

        // these are different attempts. have them commented out so as not to repeat attempts or just look at something to try again
        data.filter((photo) => {
          if (photo.albumId === 1) {
            return setImage(photo);
          } else {
            return;
          }
        });

        // i chained this forEach onto a map of the original array with if photo.albumId === 1
        //   .forEach((newPhotoArr) => {
        //     newPhotoArr.forEach((photo) => {
        //       //   setImage(photo.thumbnailUrl);
        //       console.log(photo.thumbnailUrl);
        //     });
        //   });
      });
  }, []);

  return (
    <div>
      <img src={image.image} alt={image.image} />
    </div>
  );
};

export default StoreGrid;
