import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";
import { showSlideMenu } from "../../actions";
import { Link } from "react-router-dom";
import { logout, loadUser, loadUserCart } from "../../actions/index";

// eslint-disable-next-line
import css from "../../css/header.css";

const Header = ({ loadUser, logout, showSlideMenu, authUser }) => {
  const { isAuthenticated, user } = authUser;

  useEffect(() => {
    loadUser();
    // eslint-disable-next-line
  }, [isAuthenticated]);

  const onLogout = () => {
    logout();
  };

  const open = () => {
    document.body.classList.toggle("open");
  };

  const guestLinks = () => {
    return (
      <Fragment>
        <Link to='/account/login'>
          <button className='btn btn--primary'>Login</button>
        </Link>
        <Link to='/account/sign-up'>
          <button className='btn btn--secondary'>Sign Up</button>
        </Link>
      </Fragment>
    );
  };

  const authLinks = () => {
    return (
      <div className='header__user-login'>
        <h1>Hello {user && user.name}</h1>
        <a onClick={onLogout} href='#!'>
          <svg
            xmlns='http://www.w3.org/2000/svg'
            className='h-6 w-6'
            fill='none'
            viewBox='0 0 24 24'
            stroke='currentColor'>
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              strokeWidth={2}
              d='M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1'
            />
          </svg>
          <span>Logout</span>
        </a>
      </div>
    );
  };

  const renderedItems = () => {
    return (
      <div className='header'>
        <button
          onClick={() => {
            showSlideMenu();
            open();
          }}
          className='btn hamburger header__icon--bg-color'>
          <i className='lni lni-menu'></i>
        </button>

        <h1 className='header__title'>Store Name</h1>

        <span className='header__categories'>Categories</span>

        <div className='header__btn-group'>
          <Link to='/'>
            <button className='btn header__icon--bg-color'>
              <i className='lni lni-home'></i>
            </button>
          </Link>
          <Link to='/cart'>
            <button className='btn header__icon--bg-color'>
              <i className='lni lni-cart'></i>
            </button>
          </Link>
          {isAuthenticated ? authLinks() : guestLinks()}
        </div>
      </div>
    );
  };

  return <div className='navbar bg-dark'>{renderedItems()}</div>;
};

const mapStateToProps = (state) => {
  return {
    authUser: state.authUser,
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps, {
  showSlideMenu,
  logout,
  loadUser,
  loadUserCart,
})(Header);
