import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts } from "../actions";

// eslint-disable-next-line
import css from "../css/storeGrid.css";

//? ditching the getCategories api calls to sort out by category. just going to instead match the categories to the products.category state and render out that way.
//? maybe make a helper function that renders all products that are filtered and keep the default renderImages helper function. I can maybe place these two function into a ternary that will render sortedProducts if categories is true and render renderImages if false.

const StoreGrid = ({ getProducts, products, categories }) => {
  // anytime categories state has been loaded by a user making selections from the checkbox, the getCategory fetch call will be made.
  useEffect(() => {
    getProducts();

    // eslint-disable-next-line
  }, [categories]);

  //* creating a function here that will take the products, match them to categories, then render out those sorted products
  const sortedProducts = () => {
    const sortedProducts = products.filter(
      (item) => item.category !== categories
    );
    console.log(sortedProducts);
  };

  const renderImages = () => {
    return products.map((item) => {
      return (
        <div className='col col-4 product' key={item.id}>
          <img src={item.image} alt={item.image} />
          <h6>{item.title}</h6>
          <h5>${item.price}</h5>
        </div>
      );
    });
  };

  return (
    <div className='container'>
      <div className='row'>{renderImages()}</div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
    categories: state.categories,
  };
};

export default connect(mapStateToProps, {
  getProducts,
})(StoreGrid);
