import { FETCH_CATEGORY } from "../types";

const categoryReducer = (state = [], action) => {
  switch (action.type) {
    case FETCH_CATEGORY:
      return [...state, action.payload, action.payload];
    default:
      return state;
  }
};

export default categoryReducer;
