import { CART_ITEM, REMOVE_ITEM } from "../types";

const cartItemReducer = (state = ["value"], action) => {
  switch (action.type) {
    case CART_ITEM:
      return [...state, action.payload];
    case REMOVE_ITEM:
      return action.payload;
    default:
      return state;
  }
};

export default cartItemReducer;
