import React from "react";
import { connect } from "react-redux";
import { useRef, useEffect } from "react";
// eslint-disable-next-line
import css from "../css/overlay.css";

const Overlay = ({ openOrClosed }) => {
  const ref = useRef(null);
  useEffect(() => {
    ref.current.addEventListener("click", () => console.log("clicked"));
  }, []);

  const toggleMenu = () => {
    if (openOrClosed) {
      return "overlay";
    } else {
      return "";
    }
  };

  const toggle = toggleMenu();

  return <div ref={ref} className={toggle}></div>;
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps)(Overlay);
