import { REGISTER_SUCCESS, REGISTER_FAIL } from "../types";

const initialState = {
  isAuthenticated: null,
  loading: true,
  user: null,
  error: null,
};

const authReducer = (state = [{ isAuthenticated: null }], action) => {
  switch (action.type) {
    case REGISTER_SUCCESS:
      return {
        ...state,
      };
    default:
      return state;
  }
};

export default authReducer;
