import React from "react";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = (props) => {
  const productCategories = [
    {
      category: "Mens Clothing",
    },
    {
      category: "Womens Clothing",
    },
    {
      category: "Jewlery",
    },
    {
      category: "Electronics",
    },
  ];

  const renderInput = () =>
    productCategories.map((category, index) => {
      return (
        <div className='form-check' key={index}>
          <input
            className='form-check-input'
            type='checkbox'
            value=''
            id='flexCheckDefault'
          />
          <label className='form-check-label' htmlFor='flexCheckDefault'>
            {category.category}
          </label>
        </div>
      );
    });

  const toggleMenu = () => {
    if (!props.openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle} container`}>
      <h5>Filter By Category</h5>
      <form>
        {renderInput()}
        <button className='btn btn-primary'>Apply Filter</button>
      </form>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

const slideMenu = connect(mapStateToProps)(SlideMenu);

export default reduxForm({
  form: "slideMenu",
})(slideMenu);
