import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts } from "../actions";

// eslint-disable-next-line
import css from "../css/storeGrid.css";

//? categories is the array from which the SlideMenu creates when a user makes their selections from the checkboxes. this array needs to be fed each time through the getCategory action creator
//? category is supposed to be the piece of state in which the getCategory writes to which needs to be a combined array of the categories that have been selected

const StoreGrid = ({ getProducts, products, categories }) => {
  // anytime categories state has been loaded by a user making selections from the checkbox, the getCategory fetch call will be made.
  //? this might not be the correct approach as everytime the component is loaded the getProducts call is made and the getCategory seems to only be called after the user has submitted their choice twice.
  useEffect(() => {
    getProducts();
    // eslint-disable-next-line
  }, [categories]);

  const renderImages = () => {
    return products
      .filter((item) => item.category === categories[0])
      .map((item) => {
        return (
          <div className='col col-4 product' key={item.id}>
            <img src={item.image} alt={item.image} />
            <h6>{item.title}</h6>
            <h5>${item.price}</h5>
          </div>
        );
      });
  };

  return (
    <div className='container'>
      <div className='row'>{renderImages()}</div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
    categories: state.categories,
  };
};

export default connect(mapStateToProps, {
  getProducts,
})(StoreGrid);
