import React from "react";
import { connect } from "react-redux";
import { showSlideMenu } from "../actions";
import { Link } from "react-router-dom";

// eslint-disable-next-line
import css from "../css/header.css";

const Header = (props) => {
  const open = () => {
    document.body.classList.toggle("open");
  };

  const renderedItems = () => {
    return (
      <div className='header header--bg-color'>
        <button
          onClick={() => {
            props.showSlideMenu();
            open();
          }}
          className='btn header__btn--bg-color'>
          <i className='lni lni-menu'></i>
        </button>

        <div className=''>Store Name</div>

        <div className='header__btn-group'>
          <Link to='/'>
            <button className='btn header__btn--bg-color'>
              <i className='lni lni-home'></i>
            </button>
          </Link>
          <Link to='/cart'>
            <button className='btn header__btn--bg-color'>
              <i className='lni lni-cart'></i>
            </button>
          </Link>
          <button className='btn btn--primary'>Login</button>
          <button className='btn btn--secondary'>Sign Up</button>
        </div>
      </div>
    );
  };

  return <div className='navbar bg-dark'>{renderedItems()}</div>;
};

const mapStateToProps = (state, ownProps) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps, {
  showSlideMenu,
})(Header);
