import React from "react";
import { connect } from "react-redux";

// eslint-disable-next-line
import css from "../css/slideMenu.css";

const SlideMenu = ({ openOrClosed }) => {
  const toggleMenu = () => {
    if (!openOrClosed) {
      return "slide-menu";
    } else {
      return "slide-menu open";
    }
  };

  const toggle = toggleMenu();

  return (
    <div className={`${toggle} container`}>
      <h6>Filter By Category</h6>
      <div className='form-check'>
        <input
          className='form-check-input'
          type='checkbox'
          value=''
          id='flexCheckDefault'
        />
        <label className='form-check-label' htmlFor='flexCheckDefault'>
          Mens Clothing
        </label>
      </div>
      <div className='form-check'>
        <input
          className='form-check-input'
          type='checkbox'
          value=''
          id='flexCheckChecked'
        />
        <label className='form-check-label' htmlFor='flexCheckChecked'>
          Womens Clothing
        </label>
      </div>
      <div className='form-check'>
        <input
          className='form-check-input'
          type='checkbox'
          value=''
          id='flexCheckChecked'
        />
        <label className='form-check-label' htmlFor='flexCheckChecked'>
          Jewlery
        </label>
      </div>
      <div className='form-check'>
        <input
          className='form-check-input'
          type='checkbox'
          value=''
          id='flexCheckChecked'
        />
        <label className='form-check-label' htmlFor='flexCheckChecked'>
          Electronics
        </label>
      </div>
      <button className='btn btn-primary'>Apply Filter</button>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps)(SlideMenu);
