import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts } from "../actions";
import { Link } from "react-router-dom";
// eslint-disable-next-line
import css from "../css/storeGrid.css";

const StoreGrid = ({ getProducts, products, categories }) => {
  // anytime categories state has been loaded by a user making selections from the checkbox, the getCategory fetch call will be made.
  useEffect(() => {
    getProducts();
    // eslint-disable-next-line
  }, []);

  // this function here that will take the products, match them to categories, then render out those sorted products
  const sortedProducts = () => {
    const sortedProducts = products.filter((item) =>
      categories.includes(item.category)
    );
    return sortedProducts.map((item) => {
      return (
        <div className='col col-4 product' key={item.id}>
          <Link key={item.id} to={`/products/${item.id}`}>
            <img src={item.image} alt={item.image} />
            <h6>{item.title}</h6>
            <h5>${item.price}</h5>
          </Link>
        </div>
      );
    });
  };

  const renderImages = () => {
    return products.map((item) => {
      return (
        <div className='col col-4 product' key={item.id}>
          <Link key={item.id} to={`/products/${item.id}`}>
            <img src={item.image} alt={item.image} />
            <h6>{item.title}</h6>
            <h5>${item.price}</h5>
          </Link>
        </div>
      );
    });
  };

  if (!products) {
    return <div>loading</div>;
  }

  return (
    <div className='container'>
      <div className='row'>
        {categories.length === 0 ? renderImages() : sortedProducts()}
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    products: state.products,
    categories: state.categories,
  };
};

export default connect(mapStateToProps, {
  getProducts,
})(StoreGrid);
