import { CART_ITEM } from "../types";

const cartItemReducer = (state = [], action) => {
  const cartCheck = state.filter((item) => item !== action.payload.id);
  switch (action.type) {
    case CART_ITEM:
      return [...state, action.payload];
    default:
      return state;
  }
};

export default cartItemReducer;
