import React, { useState, useEffect } from "react";

const StoreGrid = () => {
  // after this logic works i want to move it out into Redux store and import here as props
  const [images, setImage] = useState([]);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/photos/")
      .then((response) => response.json())
      .then((data) => {
        //this correctly filters all photos with the albumId of 1 and sets to a new array named albumArr. i think i now need to map that albumArr and set it to images state

        const albumArr = data.filter((album) => album.albumId === 1);

        console.log(albumArr);
        setImage(albumArr);

        // trying to map over the filtered array and set the state to photothumbnail and id
        // albumArr.map((photo) => {
        //   return setImage({ image: photo.thumbnailUrl, id: photo.id });
        // });

        //   this map sets only the last image from the api call
        // data.map((photo) =>
        //   setImage({ image: photo.thumbnailUrl, id: photo.id })
        // );
      });
  }, []);
  const renderImages = () => {
    <img src={images.image} alt={images.image} />;
  };

  return <div>{renderImages()}</div>;
};

export default StoreGrid;
