import { CART_ITEM, CART_ITEM_CHECK } from "../types";

const cartItemReducer = (
  state = [
    { image: "/images/chair.jpg", title: "Computer Chair", quantity: 1 },
  ],
  action
) => {
  switch (action.type) {
    case CART_ITEM:
      return [...state, action.payload];
    case CART_ITEM_CHECK:
      return state;
    default:
      return state;
  }
};

export default cartItemReducer;
