import React from "react";
import { connect } from "react-redux";
import { showSlideMenu } from "../../actions";
import { Link } from "react-router-dom";

// eslint-disable-next-line
import css from "../../css/header.css";

const Header = (props) => {
  const isAuthenticated = true;

  const onLogout = () => {
    console.log("asdf");
  };

  const user = {
    name: "name",
  };

  const open = () => {
    document.body.classList.toggle("open");
  };

  const guestLinks = () => {
    return (
      <div className='header__btn-group'>
        <Link to='/'>
          <button className='btn header__icon--bg-color'>
            <i className='lni lni-home'></i>
          </button>
        </Link>
        <Link to='/cart'>
          <button className='btn header__icon--bg-color'>
            <i className='lni lni-cart'></i>
          </button>
        </Link>
        <Link to='/account/login'>
          <button className='btn btn--primary'>Login</button>
        </Link>
        <Link to='/account/sign-up'>
          <button className='btn btn--secondary'>Sign Up</button>
        </Link>
      </div>
    );
  };

  const authLinks = () => {
    return (
      <div className='header__user-login'>
        <h1>Hello {user && user.name}</h1>
        <a onClick={onLogout} href='#!'>
          <i className='fas fa-sign-out-alt' />{" "}
          <span className='hide-sm'>Logout</span>
        </a>
      </div>
    );
  };

  const renderedItems = () => {
    return (
      <div className='header'>
        <button
          onClick={() => {
            props.showSlideMenu();
            open();
          }}
          className='btn header__icon--bg-color'>
          <i className='lni lni-menu'></i>
        </button>

        <div className=''>Store Name</div>

        {isAuthenticated ? authLinks() : guestLinks()}

        {/* commented out to work on a function that conditionally checks to render this */}
        {/* <div className='header__btn-group'>
          <Link to='/'>
            <button className='btn header__icon--bg-color'>
              <i className='lni lni-home'></i>
            </button>
          </Link>
          <Link to='/cart'>
            <button className='btn header__icon--bg-color'>
              <i className='lni lni-cart'></i>
            </button>
          </Link>
          <Link to='/account/login'>
            <button className='btn btn--primary'>Login</button>
          </Link>
          <Link to='/account/sign-up'>
            <button className='btn btn--secondary'>Sign Up</button>
          </Link>
        </div> */}
      </div>
    );
  };

  return <div className='navbar bg-dark'>{renderedItems()}</div>;
};

const mapStateToProps = (state) => {
  return {
    openOrClosed: state.openOrClosed,
  };
};

export default connect(mapStateToProps, {
  showSlideMenu,
})(Header);
