import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getProducts, getCategory } from "../actions";

// eslint-disable-next-line
import css from "../css/storeGrid.css";

const StoreGrid = ({
  getProducts,
  getCategory,
  products,
  category,
  categories,
}) => {
  useEffect(() => {
    getProducts();
    return () => {
      console.log("categories recieved");
